txt = ''
marker = ''
counter = 0

def has_repeated_chars(string):
    for char in string:
        if string.count(char) > 1:
            return True

    return False

for i in txt:
    counter += 1
    marker += i
    if has_repeated_chars(marker):
        marker = marker[1:]
    if len(marker) == 4:
            break

print(counter)
